// getTriggerButtonClass returns the CSS class for the trigger button
func getTriggerButtonClass(buttonProps atoms.ButtonProps) string {
	if buttonProps.Variant != "" {
		switch buttonProps.Variant {
		case "outline":
			return "btn-outline"
		case "ghost":
			return "btn-ghost"
		case "secondary":
			return "btn-secondary"
		default:
			return "btn"
		}
	}
	return "btn-outline" // Default to outline
}

// getTriggerText returns the appropriate trigger text
func getTriggerText(trigger, buttonText string) string {
	if buttonText != "" {
		return buttonText
	}
	return trigger
}

// getPopoverClass returns the CSS class for the popover without concatenation
func getPopoverClass(widthSize string) string {
	// Static mapping of width sizes
	switch widthSize {
	case "sm":
		return "popover min-w-40"
	case "md":
		return "popover min-w-56"
	case "lg":
		return "popover min-w-72"
	case "xl":
		return "popover min-w-96"
	default:
		return "popover min-w-56" // default md
	}
}

